document.addEventListener('DOMContentLoaded', function () {

        const anchor = document.querySelector('.arrow-link');
            anchor.addEventListener('click', function (e) {
                e.preventDefault();
                document.querySelector('.table').scrollIntoView({
                    behavior: 'smooth',
                    block: 'start'
                })
            });
    
    class burgerMenu {
        constructor(burgerItem, menuItems) {
            this.burger = document.querySelector(burgerItem);
            this.menuItems = document.querySelectorAll(menuItems);
            this.isChanging = false;
        }
        burgerStart() {
            let that = this,
                changingTime = 500,
                disabledTime = 4000; //время которое меню не будет откликаться на клики, чтобы не было багов
            this.burger.addEventListener('click', function () {

                if (that.isChanging == false) {
                    that.isChanging = true;
                    that.burger.classList.contains('close') ? (showOrHideMenuItems('none', 0)) : (showOrHideMenuItems('block', 1));
                }

            });

            function changeOver() {
                that.burger.style.opacity = .3;
                setTimeout(function () {
                    that.isChanging = false;
                    that.burger.style.opacity = 1;
                }, disabledTime);
            }

            function showOrHideMenuItems(displayVal, opacityVal) {


                if (displayVal) {


                    for (let item of that.menuItems) {


                        if (displayVal == 'none') {
                            that.burger.classList.remove('close');
                            setTimeout(function () {
                                item.style.display = displayVal;
                            }, changingTime);
                            setTimeout(function () {
                                item.style.opacity = opacityVal;
                            }, changingTime / 2);
                            changeOver();
                        } else {
                            that.burger.classList.add('close');
                            setTimeout(function () {
                                item.style.display = displayVal;
                            }, changingTime / 2);
                            setTimeout(function () {
                                item.style.opacity = opacityVal;
                            }, changingTime);
                            changeOver();


                        }


                    }

                }



            }
        }
    }


    let burger1 = new burgerMenu('.menu__burger','.menu__link-main'),
    burger2 = new burgerMenu('.menu__burger-footer','.menu__link-footer');
    

        
    
    burger1.burgerStart();
    burger2.burgerStart();
})
